import React, { useState } from "react";
import { View } from "react-native";
import Number from "../components/Number"
// import { CountContext } from "../context/Count";
import { Text, Box } from "native-base";

export default function Calculator() {

    const [counter, setCounter] = useState(0);

    return (
        <Box>
            <View
                padding= "30"
                width= '100%'
                height= '100%'
                backgroundColor= '#202020'
            >
                <View 
                    display= "flex"
                    justifyContent= 'center'
                    marginTop= '20'
                    backgroundColor= '#353535'
                    color= '#4f4f4f'
                    fontSize= '20'
                    height= '175'
                    width= "100%"
                    borderRadius= '10'
                >
                    <Text 
                        fontSize= '40'
                        fontWeight= 'bold'
                        margin= '15'
                        color= '#f5f5f5'
                        textAlign= 'right'
                    >   {counter}
                    </Text>
                </View>

                <View>
                    <Number/>
                </View>
            </View>


            {/* const styles = StyleSheet.create ({
                background: {
                    padding: 30,
                    width: '100%',
                    height: '100%',
                    backgroundColor: '#202020'
                },
                resultBox: {
                    display: "flex",
                    justifyContent: 'center',
                    marginTop: 20,
                    backgroundColor: '#353535',
                    color: '#4f4f4f',
                    fontSize: 20,
                    height: 175,
                    width: "100%",
                    borderRadius: 10,
                },
                resultNumber: {
                    fontSize: 40,
                    fontWeight: 'bold',
                    margin: 15,
                    color: '#f5f5f5',
                    textAlign: 'right',
                }
            }) */}
        </Box>
    );
}
