import { StatusBar } from "expo-status-bar";
import React, { useState } from "react";
import { TouchableOpacity } from "react-native";
import { Text, Box } from "native-base";
import { backgroundColor, height } from "styled-system";

export default function incDec() {
  // Init State
  const [counter, setCounter] = useState(0);

  //Create Function Add
  function Add() {
    return setCounter(counter + 1);
  }

  // Create Function Less
  function Less() {
    if (counter > 0) {
      return setCounter(counter - 1);
    }
  }

  return (
    <Box
      bg="primary.400"
      flex={1}
      alignItems="center"
      justifyContent="center"
      w="100%"
      p={10}
    >
      <StatusBar />
      {/* Code Here */}
      <Text textAlign="center">
        If you click the add button it will increase by one, vice versa if you
        click the less button it will decrease by one
      </Text>

      <Text fontSize={50} style={{ color: "#273c75" }}>
        {counter}
      </Text>

      <TouchableOpacity
        onPress={Add}
        style={{
          backgroundColor: "#487eb0",
          height: 40,
          width: "100%",
          alignItems: "center",
          justifyContent: "center",
          borderRadius: 10,
          margin: 10,
        }}
      >
        <Text color={{ color: "white" }}>Add</Text>
      </TouchableOpacity>
      <TouchableOpacity
        onPress={Less}
        style={{
          backgroundColor: "#487eb0",
          height: 40,
          width: "100%",
          alignItems: "center",
          justifyContent: "center",
          borderRadius: 10,
        }}
      >
        <Text color={{ color: "white" }}>Less</Text>
      </TouchableOpacity>
    </Box>
  );
}
